mapper.select:
SELECT *
FROM zy_file
WHERE del_flag = 0


        AND category = #{zyFileDTO.category}


        AND parent_id = #{zyFileDTO.parentId}


        AND keyword = #{zyFileDTO.keyword}


ORDER BY create_time DESC

mapper.update:
UPDATE zy_file
SET del_flag = 1
WHERE 1 = 1

    AND 1 = 0


    AND category = #{zyFileDTO.category}
    AND parent_id = #{zyFileDTO.parentId}

        AND keyword = #{zyFileDTO.keyword}

