mapper.select:
SELECT *
FROM zy_regulation_bjmoa_history zrbh
WHERE zrbh.identifier = #{identifier}
  AND zrbh.version = #{version}
  AND zrbh.del_flag = 0
ORDER BY zrbh.id DESC

SELECT *
FROM zy_regulation_bjmoa_history zrbh
WHERE zrbh.identifier = #{identifier}
  AND zrbh.version = #{version}
  AND zrbh.code = #{code}
  AND zrbh.del_flag = 0
ORDER BY zrbh.id DESC

mapper.update:
UPDATE zy_regulation_bjmoa_history
SET abolish_time = #{abolishTime}
WHERE identifier = #{identifier}
